html_part_1='<!DOCTYPE html>\n<html lang="en">\n    <head>\n        <meta charset="utf-8">\n        <title>'
html_part_2='</title>\n        <style type="text/css">\n          .end-element { fill : #FFCCFF; }\n        </style>\n        <script src="https://cdnjs.cloudflare.com/ajax/libs/raphael/2.3.0/raphael.min.js"></script>\n        <script src="https://cdnjs.cloudflare.com/ajax/libs/flowchart/1.17.1/flowchart.min.js"></script>\n        <script src="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.3.1/js/bootstrap.bundle.min.js"></script>\n        <!-- <script src="../release/flowchart.min.js"></script> -->\n        <script>\n\n            window.onload = function () {\n                var btn = document.getElementById("run"),\n                    cd = document.getElementById("code"),\n                    chart;\n\n                (btn.onclick = function () {\n                    var code = cd.value;\n\n                    if (chart) {\n                      chart.clean();\n                    }\n\n                    chart = flowchart.parse(code);\n                    chart.drawSVG(\'canvas\', {\n                      \'x\': 0,\n                      \'y\': 0,\n                      \'line-width\': 3,\n                      //\'maxWidth\': 15,//ensures the flowcharts fits within a certain width\n                      \'line-length\': 50,\n                      \'text-margin\': 10,\n                      \'font-size\': 14,\n                      \'font\': \'normal\',\n                      \'font-family\': \'Helvetica\',\n                      \'font-weight\': \'normal\',\n                      \'font-color\': \'black\',\n                      \'line-color\': \'black\',\n                      \'element-color\': \'black\',\n                      \'fill\': \'white\',\n                      \'yes-text\': \'yes\',\n                      \'no-text\': \'no\',\n                      \'arrow-end\': \'block\',\n                      \'scale\': 1,\n                      \'symbols\': {\n                        \'start\': {\n\t\t\t\t\t\t  \'font-size\': 14,\n                          \'font-color\': \'yellow\',\n                          \'element-color\': \'blue\',\n                          \'fill\': \'green\',\n\t\t\t\t\t\t  \'class\': \'start-element\'\n                        },\n                        \'inputoutput\': {\n                          \'font-color\': \'black\',\n                          \'element-color\': \'black\',\n                          \'fill\': \'bisque\'\n                        },\n                        \'operation\': {\n                          \'font-color\': \'black\',\n                          \'element-color\': \'black\',\n                          \'fill\': \'linen\'\n                        },\n                        \'subroutine\': {\n                          \'font-color\': \'black\',\n                          \'element-color\': \'blue\',\n                          \'fill\': \'lightgreen\'\n                        },\n                        \'condition\': {\n                          \'font-color\': \'red\',\n\t\t\t\t\t\t  \'element-color\': \'black\',\n                          \'fill\': \'yellow\'\n                        },\n                        \'end\':{\n\t\t\t\t\t\t  \'font-size\': 20,\n                          \'class\': \'end-element\'\n                        }\n                      },\n                      \'flowstate\' : {\n                        //\'past\' : { \'fill\' : \'#CCCCCC\', \'font-size\' : 12},\n                        //\'current\' : {\'fill\' : \'yellow\', \'font-color\' : \'red\', \'font-weight\' : \'bold\'},\n                        //\'future\' : { \'fill\' : \'#FFFF99\'},\n                        \'request\' : { \'fill\' : \'blue\'},\n                        \'invalid\': {\'fill\' : \'#444444\'},\n                        \'approved\' : { \'fill\' : \'#58C4A3\', \'font-size\' : 12, \'yes-text\' : \'APPROVED\', \'no-text\' : \'n/a\' },\n                        \'rejected\' : { \'fill\' : \'#C45879\', \'font-size\' : 12, \'yes-text\' : \'n/a\', \'no-text\' : \'REJECTED\' }\n                      }\n                    });\n\n                })();\n\n            };\n            function myFunction(event, node) {\n              console.log("You just clicked this node:", node);\n            }\n            \n        </script>\n\t\t<script>\n\t\t\tfunction HelpText() {\n\t\t\t  var x = document.getElementById("HelpTextBlock");\n\t\t\t  if (x.style.display === "none") {\n\t\t\t\tx.style.display = "block";\n\t\t\t  } else {\n\t\t\t\tx.style.display = "none";\n\t\t\t  }\n\t\t\t}\n\t\t</script>\n    </head>\n    <body>\n        <div><textarea id="code" style="width: 100%;" rows="11">'
html_part_3=')</textarea></div>\n        <div><button id="run" type="button">Run</button> <button onclick="HelpText()">Format Help</button></div>\n        \n\t\t<div id="HelpTextBlock" style="display:none">Conditions can also be redirected like cond(yes, bottom) or cond(yes, right)\n... and the other symbols too... like sub1(right)<br/>\nYou can also tweak the diagram.drawSVG(\'diagram\', {}); script section in this file for more changes to how it looks.<br/>\nThis is based on <a href="https://github.com/adrai/flowchart.js">flowchart.js on github</a> and <a href="http://flowchart.js.org">http://flowchart.js.org</a> more documentation can be found over there.\n</div>\n\n        <div id="canvas"></div>\n    </body>\n</html>'
